mapper.select:
select u.user_code driverCode, u.user_name driverName, u.user_picture_url userPictureUrl, u.tel, u.sex, u.idcard, u.user_acct driverAcct, u.version from t_sys_user as u left join t_sys_store as s on u.area_code = s.area_code and u.downtown_code = s.downtown_code and u.province_code = s.province_code and s.is_delete = 0 where u.is_delete = 0 and role = 3
and shopkeeper_code = #{updateUser}
and user_code like concat('%', #{driverCode}, '%')
and user_name like concat('%', #{driverName}, '%')
and province_code = #{provinceCode}
and downtown_code = #{downtownCode}
and area_code = #{areaCode}
order by user_code

select count(user_acct)
from t_sys_user
where is_delete = 0
and user_acct = #{driverAcct}

    and user_code != #{driverCode}
