mapper.select:
select user_code driverCode, user_name driverName, user_picture_url userPictureUrl, tel, sex, idcard, user_acct driverAcct, version from t_sys_user where is_delete = 0 and role = 3
and user_code like concat('%', #{driverCode}, '%')
and user_name like concat('%', #{driverName}, '%')
and province_code = #{provinceCode}
and downtown_code = #{downtownCode}
and area_code = #{areaCode}
order by user_code

select count(user_acct)
from t_sys_user
where is_delete = 0
and user_acct = #{driverAcct}

    and user_code != #{driverCode}
