mapper.select:
select s.store_code storeCode, s.store_name storeName, s.tel, concat(s.province_name, s.downtown_name, s.area_name, s.address) address, u.user_name shopkeeperName, s.licence_code licenceCode, s.invite_code inviteCode, u.user_acct storeAcct, s.version from t_sys_store as s LEFT JOIN t_sys_user as u on s.shopkeeper_code = u.user_code where s.is_delete = 0 -- and u.role = 2
and s.store_code like concat('%', #{storeCode}, '%')
and s.store_name like concat('%', #{storeName}, '%')
and u.user_name like concat('%', #{shopkeeperName}, '%')
and s.province_code = #{provinceCode}
and s.downtown_code = #{downtownCode}
and s.area_code = #{areaCode}
order by store_code

select count(invite_code)
from t_sys_store
where is_delete = 0
and invite_code = #{inviteCode}

    and store_code != #{storeCode}
